function decodeURLParamsTcb(url) {
    const startIndex = url.indexOf('?'); // Tìm vị trí bắt đầu của các tham số
    const paramString = url.substring(startIndex); // Lấy phần chuỗi sau dấu '?' (các tham số)
    const params = new URLSearchParams(paramString);
    const decodedParams = {};
    for (let [key, value] of params) {
      decodedParams[key] = decodeURIComponent(value);
    }
    return decodedParams;
}
            
