    $(document).on('change', '.wrap_qty .qty', function(event) {
        if($(this).prop('disabled')) return false;
        var el = $(this);        
        var qty = el.val();  
        var numberPattern = new RegExp("[0-9]+");        
        if(!numberPattern.test(qty) || isNaN(qty) || qty <= 0){
            event.preventDefault();
            $(this).val(el.data('val'));
            return false;
        }
        if(qty == '' || qty == NaN){
            qty = 1;
        }
        qty = parseInt(qty);
        if(qty < 1){
            qty = 1;
        }
        var maxQty = parseInt(el.data().max);
        if(maxQty < qty){
            qty = maxQty
            el.val(qty);
        }
        var action = el.data().action;
        var productId = el.data().productId;
        var variant = el.data().variant;
        var sku = el.data().sku;
        if(el.parents('#popup-cart').length > 0){
            action = 'update-cart';
        }
        switch(action) {
            case 'update-cart':
                updateQuantityCart(productId, qty, el, variant, sku);
                break;
            default : break;
        }
        if(action == undefined || action == ''){
            //checkShowGroupPromotion(qty);
        }
    });
