function sendQuestion(productId, content, questionId, el) {
    var checkLogin = isLogin();
    if (checkLogin == false) {
        showLoginPopup();
        return false;
    }
    $.ajax({
        async: true,
        type: 'POST',
        url: '/ajax?api=question.send',
        data: {
            parent_id : questionId,
            product_id : productId,
            content : content,
        },
        dataType: 'json',
        beforeSend: function () {},
        success: function (data, status) {
            if (status == 'success' && data.error == 0) {
                if(questionId != ''){
                    addHtmlAnswer(data.data, productId, el);
                } else {
                    addHtmlQuestion(data.data, el);
                }
                el.parent().find('textarea').val('');
            } else if(data.message == 'showLogin'){
                showLoginPopup();
            } else {
                showAlert('', data.message, 'danger');
            }
        },
        error: function () {},
        complete: function () {}
    });
}

