function isPhone(text) {
    var max = text.length;
    if(max < 9 || max > 20){
        return false;
    }
    var isCheck = true;
    for (i = 0; i < max; i++){
        var chart = text.substring(i, i+1);
        if(chart == '.' || isNaN(chart)){
            isCheck = false;
            i = max;
        }
    }
    return isCheck;
}
